mapper.select:
SELECT movie_id, poster_link, title, movie_director, movie_actor, movie_genre, age_limit, opening_date, accumul_audience, star_info, synopsis, like_count, star_info
FROM movie
WHERE movie_id=#{movie_id} AND title=#{title}

mapper.update:
UPDATE movie
SET like_count=#{like_count}
WHERE movie_id=#{movie_id}

mapper.select:
		SELECT members_id, comment, comment_date, star_comment, user_id, edit_date
		FROM star_comment
		WHERE movie_id = #{movie_id}

		ORDER BY edit_date DESC, comment_date DESC 


            LIMIT #{offset}, #{listCount}

SELECT count(*)
FROM star_comment
WHERE movie_id = #{movie_id}

mapper.update:
UPDATE star_comment
SET comment=#{comment}, edit_date=#{edit_date}
WHERE members_id=#{members_id} AND movie_id=#{movie_id}

mapper.select:
SELECT members_id, movie_id
FROM fav_movie
WHERE members_id=#{members_id} AND movie_id=#{movie_id}

SELECT *
FROM star_comment
WHERE members_id=#{members_id} AND movie_id=#{movie_id}
